          save_history_string();

          if (switch_skip_to_endcase)
            BEGIN(END_CASE_SKIP);
          else
            BEGIN(if_state[if_lvl]);
          unput('}');
          unput('O');
          unput('H');
          unput('C');
          unput('E');
          unput('{');
